window.innerWidth < 768 &&
  [].slice
    .call(document.querySelectorAll("[data-bss-disabled-mobile]"))
    .forEach(function (e) {
      e.classList.remove("animated"),
        e.removeAttribute("data-bss-hover-animate"),
        e.removeAttribute("data-aos");
    }),
  document.addEventListener(
    "DOMContentLoaded",
    function () {
      [].slice
        .call(document.querySelectorAll("[data-bss-hover-animate]"))
        .forEach(function (e) {
          e.addEventListener("mouseenter", function (e) {
            e.target.classList.add(
              "animated",
              e.target.dataset.bssHoverAnimate
            );
          }),
            e.addEventListener("mouseleave", function (e) {
              e.target.classList.remove(
                "animated",
                e.target.dataset.bssHoverAnimate
              );
            });
        });
    },
    !1
  );

// Click on a close button to hide the current list item
var close = document.getElementsByClassName("close");
var i;
for (i = 0; i < close.length; i++) {
  if (i != 1) {
    close[i].onclick = function () {
      var div = this.parentElement;
      div.style.display = "none";
      console.log("closed");
    };
  }
}

// Create a new time slot when clicking on the "Add" button
function newSlot() {
  var slot = document.getElementById("slot");
  var newSlot = slot.cloneNode(true);
  document.getElementById("container").appendChild(newSlot);

  // close button function
  for (i = 0; i < close.length; i++) {
    close[i].onclick = function () {
      var div = this.parentElement;
      div.remove();
    };
  }
}

// Create a "close" button and append it to each slot
var time = document.getElementsByClassName("time");
var i;
for (i = 0; i < time.length; i++) {
  if (1) {
    var span = document.createElement("span");
    var icon = document.createTextNode("\u00D7");
    span.className = "close";
    span.appendChild(icon);
    time[i].appendChild(span);
  }
}
